 обновленного паспорта не совпадает с фамилией, указанной на доп.защите, рекомендуем иметь при себе документ с старой фамилией, в случае, если пограничник попросит подтверждения, что речь об одном человек,это, действительно может быть и свидетельство о браке, и паспорт на старую фамилию.','ru','2025-04-08 23:45:45.432502','2025-04-08 23:45:45.432502','[-0.21152979135513306, 0.41298022866249084, -0.02287723682820797, -0.23168033361434937, 0.1654355525970459, 0.15622617304325104, 0.007269357796758413, -0.23293821513652802, -0.09263479709625244, -0.07627398520708084, -0.08800617605447769, 0.00972650945186615, 0.1336740106344223, -0.2744585871696472, -0.02833455428481102, 0.03536026552319527, -0.17961138486862183, 0.23632170259952545, -0.15293800830841064, 0.15986791253089905, -0.2520711123943329, -0.1202143058180809, 0.23108181357383728, -0.10467824339866638, 0.40117964148521423, -0.13004305958747864, 0.004417056683450937, 0.15688611567020416, -0.03682875633239746, -0.16525855660438538, 0.28476929664611816, 0.06635796278715134, -0.04798878729343414, -0.07552512735128403, -0.14282433688640594, 0.1542494148015976, -0.10524387657642365, 0.08654481917619705, 0.2546232044696808, 0.3925406336784363, 0.20208361744880676, -0.21740952134132385, -0.18963411450386047, 0.07991684973239899, 0.030645128339529037, 0.2211839109659195, 0.36167654395103455, 0.28812915086746216, -0.36920979619026184, 0.22145524621009827, -0.014722835272550583, -0.06800481677055359, -0.34539052844047546, 0.3289329707622528, -0.24725468456745148, -0.03515845909714699, 0.1852731853723526, 0.020330874249339104, -0.11643659323453903, -0.03258409723639488, -0.05327133461833, 0.022109292447566986, -0.08222022652626038, -0.049521323293447495, -0.33613383769989014, -0.10784440487623215, -0.10808905959129333, -0.14731955528259277, 0.03304116055369377, -0.07035906612873077, 0.13969722390174866, -0.0010219680843874812, -0.3648725748062134, 0.12716753780841827, 0.04210525378584862, -0.2249946892261505, -0.08118148893